mapper.select:
select pt.id, um.name, pt.period, pt.status from period_tbl pt inner join user_mst um on pt.id = um.id
pt.id = #{id}
and pt.period >= #{periodFrom} and pt.period <= #{periodTo}
and pt.period >= #{periodFrom}
and pt.period <= #{periodTo}
order by pt.id asc, pt.period asc

mapper.delete:
delete from
    period_tbl
where
    id     = #{id}
and period = #{period}

mapper.insert:
insert into period_tbl (
    id,
    period,
    status
)
values (
    #{id},
    #{period},
    #{status}
)

mapper.update:
update
    period_tbl
set
    status = #{status}
where
    id     = #{id}
and period = #{period}
